    public class ACClearingDebtsController : BaseBusinessController
    {
        public ACClearingDebtsController()
        {
            dal = new DALBaseProvider("ACClearingDebts", typeof(ACClearingDebtsInfo));
        }
        public DataSet GetClearingDebtListByBranchID(string clearingDebtNo,
                                   int? objectID,
                                   string objectType,
                                   int? employeeID,
                                   DateTime? fromDate,
                                   DateTime? toDate,
                                   int? branchID)
        {
            return dal.GetDataSet("ACClearingDebts_GetClearingDebtListByBranchID",
                                clearingDebtNo,
                                objectID,
                                objectType,
                                employeeID,
                                fromDate,
                                toDate,
                                branchID);
        }
        public DataSet GetClearingDebtListByListOfBranchID(string clearingDebtNo,
                                                                int? objectID,
                                                                string objectType,
                                                                int? employeeID,
                                                                DateTime? fromDate,
                                                                DateTime? toDate,
                                                                List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetClearingDebtListByBranchID(clearingDebtNo, objectID, objectType, employeeID, fromDate, toDate
                                                                , ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
            }

            return rtn;
        }
    }
