    public class ACEquipmentStatesController : BaseBusinessController
    {
        public ACEquipmentStatesController()
        {
            dal = new DALBaseProvider("ACEquipmentStates", typeof(ACEquipmentStatesInfo));
        }

        public List<ACEquipmentStatesInfo> GetAllEquipmentStates()
        {
            List<ACEquipmentStatesInfo> equipmentStates = new List<ACEquipmentStatesInfo>();
            DataSet ds = dal.GetAllObject();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    ACEquipmentStatesInfo objEquipmentStatesInfo = (ACEquipmentStatesInfo)GetObjectFromDataRow(row);
                    equipmentStates.Add(objEquipmentStatesInfo);
                }
            }
            return equipmentStates;
        }
    }
    #endregion
