    public class ACMoneyChangesController : BaseBusinessController
    {
        public ACMoneyChangesController()
        {
            dal = new DALBaseProvider("ACMoneyChanges", typeof(ACMoneyChangesInfo));
        }

        public DataSet GetMoneyChangesByBranchID(string moneyChangeNo, int? branchID, DateTime? fromDate, DateTime? toDate)
        {
            return dal.GetDataSet("ACMoneyChanges_GetMoneyChangesByBranchID", moneyChangeNo, branchID, fromDate, toDate);
        }

        public DataSet GetSearchData(string moneyChangeNo, List<BRBranchsInfo> branchList, DateTime? fromDate, DateTime? toDate)
        {
            DataSet results = new DataSet();
            branchList.ForEach(o => results.Merge(GetMoneyChangesByBranchID(moneyChangeNo, o.BRBranchID, fromDate, toDate)));
            return results;
        }
    }
    #endregion
