    public class ACRelativeDocumentsController : BaseBusinessController
    {
        public ACRelativeDocumentsController()
        {
            dal = new DALBaseProvider("ACRelativeDocuments", typeof(ACRelativeDocumentsInfo));
        }

        /// <summary>
        /// Get relative document by document id
        /// </summary>
        /// <param name="documentID">Document id</param>
        /// <returns>List of the relative document</returns>
        public List<ACRelativeDocumentsInfo> GetRelativeDocumentByDocumentID(int documentID)
        {
            List<ACRelativeDocumentsInfo> relativeDocumentList = new List<ACRelativeDocumentsInfo>();
            DataSet ds = dal.GetDataSet("ACRelativeDocuments_GetRelativeDocumentByDocumentID", documentID);
            if (ds.Tables[0].Rows.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    ACRelativeDocumentsInfo objRelativeDocumentsInfo = (ACRelativeDocumentsInfo)GetObjectFromDataRow(row);
                    relativeDocumentList.Add(objRelativeDocumentsInfo);
                }
            }
            return relativeDocumentList;
        }

        /// <summary>
        /// Get object by source document id and id of relative document
        /// </summary>
        /// <param name="documentID">Source document id</param>
        /// <param name="relativeDocumentID">Id of document is relative to the source document</param>
        /// <returns>Relative document object</returns>
        public ACRelativeDocumentsInfo GetObjectByDocumentIDAndRelativeDocumentID(int documentID, int relativeDocumentID)
        {
            DataSet ds = dal.GetDataSet("ACRelativeDocuments_GetObjectByDocumentIDAndRelativeDocumentID", documentID, relativeDocumentID);
            if (ds.Tables.Count > 0 && ds.Tables[0].Rows.Count > 0)
            {
                ACRelativeDocumentsInfo objRelativeDocumentsInfo = (ACRelativeDocumentsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[0]);
                return objRelativeDocumentsInfo;
            }
            return null;
        }
    }
