    public class ACStopDepreciationAssetsController : BaseBusinessController
    {
        public ACStopDepreciationAssetsController()
        {
            dal = new DALBaseProvider("ACStopDepreciationAssets", typeof(ACStopDepreciationAssetsInfo));
        }

        public DataSet GetStopDepreciationAssetListByListOfBranchID(
                                                               string stopDepreciationAssetNo,
                                                               int? assetID,
                                                               DateTime? dateFrom,
                                                               DateTime? dateTo,
                                                               int? branchID,
                                                               List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetStopDepreciationAssetByBranchID(stopDepreciationAssetNo, assetID, dateFrom, dateTo, ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
            }

            return rtn;
        }

        public DataSet GetStopDepreciationAssetByBranchID(
                                                           string stopDepreciationAssetNo,
                                                           int? assetID,
                                                           DateTime? dateFrom,
                                                           DateTime? dateTo,
                                                           int? branchID)
        {
            DataSet ds = dal.GetDataSet("ACStopDepreciationAssets_GetStopDepreciationAssetByBranchID",
                                            stopDepreciationAssetNo,
                                            assetID,
                                            dateFrom,
                                            dateTo,
                                            branchID);
            return ds;
        }
    }
