        public DataSet GetAllocationCostListByBranchID(
                                                 string claimNo,
                                                  int? acObjectID,
                                                 string acObjectType,
                                                 int? branchID,
                                                 DateTime? claimDateFrom,
                                                 DateTime? claimDateTo)
        {
            DataSet ds = dal.GetDataSet("APAllocationCosts_GetAllocationCostListByBranchID",
                                            claimNo,
                                            acObjectID,
                                            acObjectType,
                                           branchID,
                                            claimDateFrom,
                                            claimDateTo);
            return ds;
        }

        public DataSet GetAllocationCostListByListOfBranchID(
                                                  string claimNo,
                                                  int? acObjectID,
                                                  string acObjectType,
                                                  int? branchID,
                                                  DateTime? claimDateFrom,
                                                  DateTime? claimDateTo,
                                                  List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetAllocationCostListByBranchID(claimNo, acObjectID, acObjectType, ojbBranchsInfo.BRBranchID, claimDateFrom, claimDateTo);
                rtn.Merge(ds);
                ds.Dispose();
            }

            return rtn;
        }
