            DataSet ds = dal.GetDataSet("APDocumentPayments_GetDocumentPaymentsBySupplierPaymentID", supplierPaymentID);
            return (List<APDocumentPaymentsInfo>)GetListFromDataSet(ds);
        }

        public override System.Collections.IList GetListFromDataSet(DataSet ds)
        {
            List<APDocumentPaymentsInfo> payments = new List<APDocumentPaymentsInfo>();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    APDocumentPaymentsInfo objDocumentPaymentsInfo = (APDocumentPaymentsInfo)GetObjectFromDataRow(row);
                    payments.Add(objDocumentPaymentsInfo);
