    public class APPriceSheetsController : BaseBusinessController
    {
        public APPriceSheetsController()
        {
            dal = new DALBaseProvider("APPriceSheets", typeof(APPriceSheetsInfo));
        }

        public DataSet GetPriceSheetListByBranchID(string documentNo,
                                                       string documentName,
                                                       int fk_hremployeeID,
                                                       DateTime? documentFrom,
                                                       DateTime? documentTo,
                                                       int? branchID)
        {

            return dal.GetDataSet("APPriceSheets_GetPriceSheetListByBranchID", documentNo, documentName, fk_hremployeeID, documentFrom, documentTo, branchID);
        }

        public DataSet GetPriceSheetListByListOfBranchID(string documentNo,
                                                           string documentName,
                                                       int fk_hremployeeID,
                                                           DateTime? documentFrom,
                                                           DateTime? documentTo,
                                                           List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetPriceSheetListByBranchID(documentNo, documentName, fk_hremployeeID, documentFrom, documentTo, ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
            }

            return rtn;
        }
        public List<APPriceSheetsInfo> GetListPriceSheetIsValidated(DateTime? fromDate, DateTime? toDate, int priceSheetID)
        {
            DataSet ds = dal.GetDataSet("APPriceSheets_GetListPriceSheetIsValidated", fromDate, toDate, priceSheetID);
            List<APPriceSheetsInfo> result = new List<APPriceSheetsInfo>();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    APPriceSheetsInfo objPriceSheetsInfo = (APPriceSheetsInfo)GetObjectFromDataRow(row);
                    result.Add(objPriceSheetsInfo);
                }
            }
            return result;
        }
    }
    #endregion
