    public class APSupplierPaymentDetailsController : BaseBusinessController
    {
        public APSupplierPaymentDetailsController()
        {
            dal = new DALBaseProvider("APSupplierPaymentDetails", typeof(APSupplierPaymentDetailsInfo));
        }

        /// <summary>
        /// Get default payment details including all payment methods
        /// </summary>
        /// <returns>List of payment details</returns>
        public List<APSupplierPaymentDetailsInfo> GetDefaultPaymentDetails()
        {
            List<APSupplierPaymentDetailsInfo> result = new List<APSupplierPaymentDetailsInfo>();
            ADConfigValuesController objADConfigValuesController = new ADConfigValuesController();
            DataSet ds = objADConfigValuesController.GetADConfigValuesByGroup(ADConfigValueUtility.ADConfigValuePaymentMethod);
            foreach (DataRow configRow in ds.Tables[0].Rows)
            {
                ADConfigValuesInfo objConfigValuesInfo = (ADConfigValuesInfo)objADConfigValuesController.GetObjectFromDataRow(configRow);
                if (objConfigValuesInfo.IsActive)
                {
                    APSupplierPaymentDetailsInfo objSupplierPaymentDetailsInfo = new APSupplierPaymentDetailsInfo();
                    objSupplierPaymentDetailsInfo.APPaymentMethodCombo = objConfigValuesInfo.ADConfigKeyValue;
                    result.Add(objSupplierPaymentDetailsInfo);
                }
            }
            return result;
        }

        /// <summary>
        /// Get payment details of an invoice
        /// </summary>
        /// <param name="invoiceID">Invoice id</param>
        /// <returns>Payment details</returns>
        public List<APSupplierPaymentDetailsInfo> GetPaymentDetailsByInvoiceID(int invoiceID)
        {
            DataSet ds = dal.GetDataSet("APSupplierPaymentDetails_GetPaymentDetailsByInvoiceID", invoiceID);
            return (List<APSupplierPaymentDetailsInfo>)GetListFromDataSet(ds);
        }

        /// <summary>
        /// Get details of a payment
        /// </summary>
        /// <param name="supplierPaymentID">Payment id</param>
        /// <returns>List of payment details</returns>
        public List<APSupplierPaymentDetailsInfo> GetDetailsByPaymentID(int supplierPaymentID)
        {
            DataSet ds = dal.GetDataSet("APSupplierPaymentDetails_GetDetailsByPaymentID", supplierPaymentID);
            return (List<APSupplierPaymentDetailsInfo>)GetListFromDataSet(ds);
        }

        public override System.Collections.IList GetListFromDataSet(DataSet ds)
        {
            List<APSupplierPaymentDetailsInfo> paymentDetails = new List<APSupplierPaymentDetailsInfo>();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    APSupplierPaymentDetailsInfo objSupplierPaymentDetailsInfo = (APSupplierPaymentDetailsInfo)GetObjectFromDataRow(row);
                    paymentDetails.Add(objSupplierPaymentDetailsInfo);
                }
            }
            ds.Dispose();
            return paymentDetails;
        }

        /// <summary>
        /// Delete all details and relative data of a payment
        /// </summary>
        /// <param name="supplierPaymentID">Payment id</param>
        public void DeleteBySupplierPaymentID(int supplierPaymentID)
        {
            dal.ExecuteStoredProcedure("APSupplierPaymentDetails_DeleteBySupplierPaymentID", supplierPaymentID);
        }
    }
