    public class ARAllocationsController : BaseBusinessController
    {
        public ARAllocationsController()
        {
            dal = new DALBaseProvider("ARAllocations", typeof(ARAllocationsInfo));
        }

        public DataSet GetAllocationListByListOfBranchID(string allocationNo,
                                                           string allocationName,
                                                           int? employeeID,
                                                           DateTime? allocationDateFrom,
                                                           DateTime? allocationDateTo,
                                                           int? branchID,
                                                           List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetAllocationListByBranchID(allocationNo, allocationName, employeeID, allocationDateFrom, allocationDateTo, ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
            }

            return rtn;
        }

        public DataSet GetAllocationListByBranchID(string allocationNo,
                                                   string allocationName,
                                                   int? employeeID,
                                                   DateTime? allocationDateFrom,
                                                   DateTime? allocationDateTo,
                                                   int? branchID)
        {
            return dal.GetDataSet("ARAllocations_GetAllocationListByListOfBranchID", allocationNo, allocationName, employeeID, allocationDateFrom, allocationDateTo, branchID);
        }
    }
    #endregion
}