    public class ARClearInvoicesController : BaseBusinessController
    {
        public ARClearInvoicesController()
        {
            dal = new DALBaseProvider("ARClearInvoices", typeof(ARClearInvoicesInfo));
        }
        public DataSet GetClearingDebtListByBranchID(string clearInvoiceInNo,
                                   int? objectID,
                                   string objectType,
                                   int? employeeID,
                                   DateTime? fromDate,
                                   DateTime? toDate,
                                   int? branchID)
        {
            return dal.GetDataSet("ARClearInvoices_GetClearInvoiceListByBranchID",
                                clearInvoiceInNo,
                                objectID,
                                objectType,
                                employeeID,
                                fromDate,
                                toDate,
                                branchID);
        }
        public DataSet GetClearingDebtListByListOfBranchID(string clearInvoiceInNo,
                                                                int? objectID,
                                                                string objectType,
                                                                int? employeeID,
                                                                DateTime? fromDate,
                                                                DateTime? toDate,
                                                                List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetClearingDebtListByBranchID(clearInvoiceInNo, objectID, objectType, employeeID, fromDate, toDate
                                                                , ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
            }

            return rtn;
        }
    }
