    public class ARPOSPaymentDetailsController : BaseBusinessController
    {
        public ARPOSPaymentDetailsController()
        {
            dal = new DALBaseProvider("ARPOSPaymentDetails", typeof(ARPOSPaymentDetailsInfo));
        }

        public override System.Collections.IList GetListFromDataSet(DataSet ds)
        {
            List<ARPOSPaymentDetailsInfo> paymentDetails = new List<ARPOSPaymentDetailsInfo>();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    ARPOSPaymentDetailsInfo objPOSPaymentDetailsInfo = (ARPOSPaymentDetailsInfo)GetObjectFromDataRow(row);
                    paymentDetails.Add(objPOSPaymentDetailsInfo);
                }
            }
            ds.Dispose();
            return paymentDetails;
        }

        public List<ARPOSPaymentDetailsInfo> GetDetailsByPaymentID(int invoiceID)
        {
            DataSet ds = dal.GetDataSet("ARPOSPaymentDetails_GetDetailsByPaymentID", invoiceID);
            return (List<ARPOSPaymentDetailsInfo>)GetListFromDataSet(ds);
        }

        public List<ARPOSPaymentDetailsInfo> GetDefaultPaymentDetails()
        {
            List<ARPOSPaymentDetailsInfo> result = new List<ARPOSPaymentDetailsInfo>();
            ADConfigValuesController objADConfigValuesController = new ADConfigValuesController();
            DataSet ds = objADConfigValuesController.GetADConfigValuesByGroup(ADConfigValueUtility.ADConfigValuePaymentMethod);
            foreach (DataRow configRow in ds.Tables[0].Rows)
            {
                ADConfigValuesInfo objConfigValuesInfo = (ADConfigValuesInfo)objADConfigValuesController.GetObjectFromDataRow(configRow);
                if (objConfigValuesInfo.IsActive)
                {
                    ARPOSPaymentDetailsInfo objPOSPaymentDetailsInfo = new ARPOSPaymentDetailsInfo();
                    objPOSPaymentDetailsInfo.ARPOSPaymentMethodCombo = objConfigValuesInfo.ADConfigKeyValue;
                    objPOSPaymentDetailsInfo.ARPaymentMethodDisplayText = objConfigValuesInfo.ADConfigText;
                    result.Add(objPOSPaymentDetailsInfo);
                }
            }
            return result;
        }
    }
    #endregion
}