    public class ARPriceSheetItemWorksController : BaseBusinessController
    {
        public ARPriceSheetItemWorksController()
        {
            dal = new DALBaseProvider("ARPriceSheetItemWorks", typeof(ARPriceSheetItemWorksInfo));
        }

        public List<ARPriceSheetItemWorksInfo> GetItemWorksByPriceSheetID(int priceSheetID)
        {
            DataSet ds = dal.GetDataSet("ARPriceSheetItemWorks_GetPriceSheetItemWorksByPriceSheetID", priceSheetID);
            List<ARPriceSheetItemWorksInfo> itemWorks = new List<ARPriceSheetItemWorksInfo>();
            if (ds.Tables[0].Rows.Count > 0)
            {
                ARPriceSheetItemWorksController objPriceSheetItemWorksController = new ARPriceSheetItemWorksController();
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    ARPriceSheetItemWorksInfo objPriceSheetItemWorksInfo = (ARPriceSheetItemWorksInfo)objPriceSheetItemWorksController.GetObjectFromDataRow(row);
                    itemWorks.Add(objPriceSheetItemWorksInfo);
                }
            }
            return itemWorks;
        }
    }
    #endregion
