    public class ARSFItemComponentsController : BaseBusinessController
    {
        public ARSFItemComponentsController()
        {
            dal = new DALBaseProvider("ARSFItemComponents", typeof(ARSFItemComponentsInfo));
        }

        /// <summary>
        /// Get sale order item component list by sale order item id
        /// </summary>
        /// <param name="saleOrderItemID">ID of sale order item</param>
        /// <returns>Sale order item component list</returns>
        public List<ARSFItemComponentsInfo> GetSFItemComponentListBySFItemID(int saleForecastItemID)
        {
            DataSet ds = dal.GetDataSet("ARSFItemComponents_GetSFItemComponentListBySFItemID", saleForecastItemID);
            List<ARSFItemComponentsInfo> components = new List<ARSFItemComponentsInfo>();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    ARSFItemComponentsInfo objSFItemComponentInfo = (ARSFItemComponentsInfo)GetObjectFromDataRow(row);
                    components.Add(objSFItemComponentInfo);
                }
            }
            return components;
        }

        /// <summary>
        /// Update shipped quantity of a component
        /// </summary>
        /// <param name="componentID">Component id</param>
        /// <param name="shippedQty">Updated shipped quantity</param>
        public void UpdateShippedQty(int componentID, decimal shippedQty)
        {
            dal.ExecuteStoredProcedure("ARSFItemComponents_UpdateShippedQty", componentID, shippedQty);
        }

        public void UpdateSOQty(int componentID, decimal SOQty)
        {
            dal.ExecuteStoredProcedure("ARSFItemComponents_UpdateSOQty", componentID, SOQty);
        }
    }
    #endregion
}