    public class CSCashFundsController : BaseBusinessController
    {
        public CSCashFundsController()
        {
            dal = new DALBaseProvider("CSCashFunds", typeof(CSCashFundsInfo));
        }

        /// <summary>
        /// Get all cash funds
        /// </summary>
        /// <returns>List of all cash funds</returns>
        public List<CSCashFundsInfo> GetAllCashFunds()
        {
            DataSet ds = GetAllObjects();
            return (List<CSCashFundsInfo>)GetListFromDataSet(ds);
        }

        public override System.Collections.IList GetListFromDataSet(DataSet ds)
        {
            List<CSCashFundsInfo> cashFunds = new List<CSCashFundsInfo>();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    CSCashFundsInfo cashFund = (CSCashFundsInfo)GetObjectFromDataRow(row);
                    cashFunds.Add(cashFund);
                }
            }
            ds.Dispose();
            return cashFunds;
        }

        public List<CSCashFundsInfo> GetListCashFundsByBranchID(int branchID)
        {
            DataSet ds = GetDataSetCashFundsByBranchID(branchID);
            return (List<CSCashFundsInfo>)GetListFromDataSet(ds);
        }

        public DataSet GetDataSetCashFundsByBranchID(int branchID)
        {
            return dal.GetDataSet("CSCashFunds_GetListCashFundsByBranchID", branchID);
        }
    }
    #endregion
