    public class GEVersionsController : BaseBusinessController
    {
        public GEVersionsController()
        {
            dal = new DALBaseProvider("GEVersions", typeof(GEVersionsInfo));
        }

        public bool CheckVersion(string version)
        {
            DataSet ds = dal.GetAllObject();
            if (ds.Tables[0].Rows.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    GEVersionsInfo objVersionsInfo = (GEVersionsInfo)GetObjectFromDataRow(row);
                    if (objVersionsInfo.GEVersionActive && version.Equals(objVersionsInfo.ToString()))
                    {
                        return true;
                    }
                }
            }
            return false;
        }
    }
    #endregion
}