        /// <param name="dateFrom">Date From</param>
        /// <param name="dateTo">To date</param>
        /// <returns>List of discipline of employee</returns>
        public DataSet GetDisciplineListByEmployeeIDAndDisciplineFromDate(int employeeID, DateTime dateFrom, DateTime dateTo)
        {
            return dal.GetDataSet("HRDisciplines_GetDisciplineListByEmployeeIDAndDisciplineFromDate", employeeID, dateFrom, dateTo);
        }
        /// <summary>
        /// Get discipline list by branch id
        /// </summary>
        /// <param name="disciplineNo">disciplineNo</param>
        /// <param name="disciplineName">disciplineName</param>
        /// <param name="branchID">branchID</param>
        /// <returns>Discipline list </returns>
        public DataSet GetDisciplineListByBranchID(
                                                   string disciplineNo,
                                                   string disciplineName,
                                                   int? branchID)
        {
            return dal.GetDataSet("HRDisciplines_GetDisciplineListByBranchID", disciplineNo, disciplineName, branchID);
        }
        /// <summary>
        /// Get discipline list by branch id
        /// </summary>
        /// <param name="disciplineNo">disciplineNo</param>
        /// <param name="disciplineName">disciplineName</param>
        /// <param name="branchList">branchList</param>
        /// <returns>Discipline list</returns>
        public DataSet GetDisciplineListByListOfBranchID(string disciplineNo,
                                                           string disciplineName,
                                                           List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetDisciplineListByBranchID(disciplineNo, disciplineName, ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
                ds.Dispose();
            }

            return rtn;
        }

        public List<HRDisciplinesInfo> GetDisciplineListBySomeCriteria(int employeeID, DateTime dateFrom, DateTime dateTo)
        {
            DataSet ds = dal.GetDataSet("HRDisciplines_GetDisciplineListByEmployeeIDAndDisciplineFromDate", employeeID, dateFrom, dateTo);
            List<HRDisciplinesInfo> EmployeeDiscipline = new List<HRDisciplinesInfo>();
            if (ds.Tables[0].Rows.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    HRDisciplinesInfo objTimeSheetEntrysInfo = (HRDisciplinesInfo)GetObjectFromDataRow(row);
                    EmployeeDiscipline.Add(objTimeSheetEntrysInfo);
                }
            }
            ds.Dispose();
            return EmployeeDiscipline;
        }

        public DataSet GetDisciplineListByEmployeeIDAndDisciplineDate(int employeeID, DateTime date)
        {
            return dal.GetDataSet("HRDisciplines_GetDisciplineListByEmployeeIDAndDisciplineDate", employeeID, date);
        }
    }
