        public DataSet GetLeaveApplicationListDatasetByListOfBranchID(string leaveApplicationNo,
                                                                string leaveApplicationName,
                                                                DateTime? fromDate,
                                                                DateTime? toDate,
                                                                List<BRBranchsInfo> branchList)
