    public class HRRecruitmentPlansController : BaseBusinessController
    {
        public HRRecruitmentPlansController()
        {
            dal = new DALBaseProvider("HRRecruitmentPlans", typeof(HRRecruitmentPlansInfo));
        }

        public override System.Collections.IList GetListFromDataSet(DataSet ds)
        {
            List<HRRecruitmentPlansInfo> RecruitmentPlans = new List<HRRecruitmentPlansInfo>();
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    HRRecruitmentPlansInfo objRecruitmentPlansInfo = (HRRecruitmentPlansInfo)GetObjectFromDataRow(row);
                    RecruitmentPlans.Add(objRecruitmentPlansInfo);
                }
            }
            ds.Dispose();
            return RecruitmentPlans;
        }

        public DataSet GetRecruitmentPlanListDatasetByListOfBranchID(string recruitmentPlanNo,
                                                                DateTime? fromDate,
                                                                DateTime? toDate,
                                                                List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetRecruitmentPlanListDataset(recruitmentPlanNo,
                                                        fromDate,
                                                        toDate,
                                                        ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
            }

            return rtn;
        }

        public DataSet GetRecruitmentPlanListDataset(string recruitmentPlanNo, DateTime? fromDate, DateTime? toDate, int brandID)
        {
            return dal.GetDataSet("HRRecruitmentPlans_GetRecruitmentPlanListDataset", recruitmentPlanNo, fromDate, toDate, brandID);
        }
    }
    #endregion
