    public class HRRewardsController : BaseBusinessController
    {
        public HRRewardsController()
        {
            dal = new DALBaseProvider("HRRewards", typeof(HRRewardsInfo));
        }

        /// <summary>
        /// Get list of rewards of an employee from date to date
        /// </summary>
        /// <param name="employeeID">ID of employee</param>
        /// <param name="dateFrom">Date from</param>
        /// <param name="dateTo">To date</param>
        /// <returns>List of reward of employee</returns>
        public DataSet GetRewardListByEmployeeIDAndRewardFromDate(int employeeID, DateTime dateFrom, DateTime dateTo)
        {
            return dal.GetDataSet("HRRewards_GetRewardListByEmployeeIDAndRewardFromDate", employeeID, dateFrom, dateTo);
        }
        /// <summary>
        /// Get Reward list by branch id
        /// </summary>
        /// <param name="RewardNo">rewardNo</param>
        /// <param name="RewardName">rewardName</param>
        /// <param name="branchID">branchID</param>
        /// <returns>Reward list </returns>
        public DataSet GetRewardListByBranchID(
                                                   string rewardNo,
                                                   string rewardName,
                                                   int? branchID)
        {
            return dal.GetDataSet("HRRewards_GetRewardListByBranchID", rewardNo, rewardName, branchID);
        }
        /// <summary>
        /// Get Reward list by branch id
        /// </summary>
        /// <param name="rewardNo">rewardNo</param>
        /// <param name="RewardName">rewardName</param>
        /// <param name="branchList">branchList</param>
        /// <returns>Reward list</returns>
        public DataSet GetRewardListByListOfBranchID(string rewardNo,
                                                           string rewardName,
                                                           List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetRewardListByBranchID(rewardNo, rewardName, ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
                ds.Dispose();
            }

            return rtn;
        }

        public DataSet GetRewardListByEmployeeIDAndRewardDate(int employeeID, DateTime date)
        {
            return dal.GetDataSet("HRRewards_GetRewardListByEmployeeIDAndRewardDate", employeeID, date);
        }
    }
    #endregion
}