        /// <param name="userGroupID">User group id</param>
        /// <returns>Data of the inventory permission</returns>
        public List<ICInventoryPermissionsInfo> GetInventoryPermissionByUserGroupID(int userGroupID)
        {
            DataSet ds = dal.GetDataSet("ICInventoryPermissions_GetInventoryPermissionByUserGroupID", userGroupID);
            List<ICInventoryPermissionsInfo> invPermissions = new List<ICInventoryPermissionsInfo>();
            if (ds.Tables.Count > 0)
