                    ICInventoryPermissionsInfo objInventoryPermissionsInfo = (ICInventoryPermissionsInfo)GetObjectFromDataRow(row);
                    invPermissions.Add(objInventoryPermissionsInfo);
                }
            }
            return invPermissions;
        }
