    public class ICProductFilesController : BaseBusinessController
    {
        public ICProductFilesController()
        {
            dal = new DALBaseProvider("ICProductFiles", typeof(ICProductFilesInfo));
        }

        public List<ICProductFilesInfo> GetICProductFilesByProductID(int productID)
        {
            List<ICProductFilesInfo> productFiles = new List<ICProductFilesInfo>();
            DataSet results = dal.GetDataSet("ICProductFiles_GetProductFilesByProductID", productID);

            if (results.Tables.Count > 0)
            {
                foreach (DataRow row in results.Tables[0].Rows)
                {
                    ICProductFilesInfo objProductFilesInfo = (ICProductFilesInfo)GetObjectFromDataRow(row);
                    productFiles.Add(objProductFilesInfo);
                }
            }
            return productFiles;
        }

        public void DeleteObjectByGUID(Guid productFileName)
        {
            dal.ExecuteStoredProcedure("ICProductFiles_DeleteObjectByGUID", productFileName);
        }
    }
    #endregion
