        public List<ICReceiptItemErrorsInfo> GetErrorByBatchProductID(int batchProductID)
        {
            List<ICReceiptItemErrorsInfo> invoices = new List<ICReceiptItemErrorsInfo>();
            DataSet ds = dal.GetDataSet("ICReceiptItemErrors_GetErrorByBatchProductID", batchProductID);
            if (ds.Tables.Count > 0)
            {
                foreach (DataRow row in ds.Tables[0].Rows)
                {
                    ICReceiptItemErrorsInfo objInvoicesInfo = (ICReceiptItemErrorsInfo)GetObjectFromDataRow(row);
                    invoices.Add(objInvoicesInfo);
                }
            }
            ds.Dispose();
            return invoices;
        }


