    public class ICReturnShippingsController : BaseBusinessController
    {
        public ICReturnShippingsController()
        {
            dal = new DALBaseProvider("ICReturnShippings", typeof(ICReturnShippingsInfo));
        }

        public DataSet GetReturnShippingListByListOfBranchID(string returnShippingNo,
                                                           string returnShippingName,
                                                           int? employeeID,
                                                           DateTime? returnShippingDateFrom,
                                                           DateTime? returnShippingDateTo,
                                                           int? branchID,
                                                           List<BRBranchsInfo> branchList)
        {
            DataSet rtn = new DataSet();
            foreach (BRBranchsInfo ojbBranchsInfo in branchList)
            {
                DataSet ds = GetReturnShippingListByBranchID(returnShippingNo, returnShippingName, employeeID, returnShippingDateFrom, returnShippingDateTo, ojbBranchsInfo.BRBranchID);
                rtn.Merge(ds);
            }

            return rtn;
        }

        public DataSet GetReturnShippingListByBranchID(string returnShippingNo,
                                                   string returnShippingName,
                                                   int? employeeID,
                                                   DateTime? returnShippingDateFrom,
                                                   DateTime? returnShippingDateTo,
                                                   int? branchID)
        {
            return dal.GetDataSet("ICReturnShippings_GetReturnShippingListByListOfBranchID", returnShippingNo, returnShippingName, employeeID, returnShippingDateFrom, returnShippingDateTo, branchID);
        }
    }
