    public class MMPrintRoutingsController : BaseBusinessController
    {
        public MMPrintRoutingsController()
        {
            dal = new DALBaseProvider("MMPrintRoutings", typeof(MMPrintRoutingsInfo));
        }
        public int GetBarcodeMax()
        {
            int value = 0;
            DataSet ds = dal.GetDataSet("MMPrintRoutings_GetBarcodeMax");
            foreach (DataRow row in ds.Tables[0].Rows)
            {
                MMPrintRoutingsInfo obj = (MMPrintRoutingsInfo)GetObjectFromDataRow(row);
                int.TryParse(obj.MMPrintRoutingBarcode, out value);
                return value;
            }
            return value;
        }
    }
