    public class ContainerModule : BaseModuleERP
    {

        public ContainerModule()
        {
            Name = "Container";
            CurrentModuleEntity = new ContainerEntities();
            CurrentModuleEntity.Module = this;
            InitializeModule();
        }

        public void CalculationQuantity()
        {
            ContainerEntities entity = (ContainerEntities)CurrentModuleEntity;
            GEContainersInfo mainObject = (GEContainersInfo)entity.MainObject;
            mainObject.GEContainerQuantity = mainObject.GEContainerHeight * mainObject.GEContainerWidth * mainObject.GEContainerLenght;
            entity.UpdateMainObjectBindingSource();
        }
    }

    #endregion
}