using BOSERP.Modules.HR.EmployeePieceWork.Localization;
using Localization;
using System.Collections.Generic;
using System.Windows.Forms;

namespace BOSERP.Modules.EmployeePieceWork
{
    #region EmployeePieceWorkModule
    //-----------------------------------------------------------
    //Generated By: BOS Studio
    //Class:EmployeePieceWorkModule
    //Created Date:Thursday, December 19, 2013
    //-----------------------------------------------------------

    public class EmployeePieceWorkModule : BaseModuleERP
    {

        public EmployeePieceWorkModule()
        {
            Name = "EmployeePieceWork";
            CurrentModuleEntity = new EmployeePieceWorkEntities();
            CurrentModuleEntity.Module = this;
            InitializeModule();
        }

        /// <summary>
        /// Remove Selected Item From Batch Input Items List
        /// </summary>
        public void DeleteItemFromEmployeePieceWorksList()
        {
            EmployeePieceWorkEntities entity = (EmployeePieceWorkEntities)CurrentModuleEntity;
            entity.EmployeePieceWorksList.RemoveSelectedRowObjectFromList();
        }
        public override void ActionNew()
        {
            base.ActionNew();
            HREmployeePieceWorksInfo objEmployeePieceWorksInfo = (HREmployeePieceWorksInfo)CurrentModuleEntity.MainObject;
            objEmployeePieceWorksInfo.HREmployeePieceWorkQuantity = 1;
        }
        /// <summary>
        /// Check exist piece work
        /// </summary>
        /// <returns></returns>
        public bool CheckExistPieceWork()
        {
            HREmployeePieceWorksInfo objEmployeePieceWorksInfo = (HREmployeePieceWorksInfo)CurrentModuleEntity.MainObject;
            HREmployeePieceWorksController objEmployeePieceWorksController = new HREmployeePieceWorksController();
            List<HREmployeePieceWorksInfo> employeePieceWorkList = new List<HREmployeePieceWorksInfo>();
            if (objEmployeePieceWorksInfo.FK_ADMatchCodeBatchComponent > 0)
                employeePieceWorkList = objEmployeePieceWorksController.GetEmployeePieceWorkByProductAndOperationToCheck(objEmployeePieceWorksInfo.FK_ICProductID,
                                                                                        objEmployeePieceWorksInfo.FK_MMOperationID,
                                                                                        objEmployeePieceWorksInfo.FK_ADMatchCodeBatchComponent
                                                                                        , objEmployeePieceWorksInfo.HREmployeePieceWorkFromDate
                                                                                        , objEmployeePieceWorksInfo.HREmployeePieceWorkToDate);
            else
            {
                employeePieceWorkList = objEmployeePieceWorksController.GetEmployeePieceWorkByProductAndOperationNotComponentToCheck(objEmployeePieceWorksInfo.FK_ICProductID,
                                                                                            objEmployeePieceWorksInfo.FK_MMOperationID,
                                                                                            objEmployeePieceWorksInfo.HREmployeePieceWorkFromDate,
                                                                                            objEmployeePieceWorksInfo.HREmployeePieceWorkToDate);

            }
            if (employeePieceWorkList != null && employeePieceWorkList.Count > 0)
            {
                return true;
            }
            return false;
        }
        public override int ActionSave()
        {
            HREmployeePieceWorksInfo objEmployeePieceWorksInfo = (HREmployeePieceWorksInfo)CurrentModuleEntity.MainObject;
            if (CheckExistPieceWork() && objEmployeePieceWorksInfo.HREmployeePieceWorkID == 0)
            {
                MessageBox.Show(EmployeePieceWorkLocalizeResources.EmployeePieceWorkExist,
                               CommonLocalizedResources.MessageBoxDefaultCaption,
                               MessageBoxButtons.OK,
                               MessageBoxIcon.Error);
                return 0;
            }
            return base.ActionSave();
        }
    }
    #endregion
}