    public class ICAdjustmentItemsController : BaseBusinessController
    {
        public ICAdjustmentItemsController()
        {
            dal = new DALBaseProvider("ICAdjustmentItems", typeof(ICAdjustmentItemsInfo));
        }


        public DataSet GetAdjustmentItemsByProductSerialNoAndStockID(
                                                            String strProductSerialNo,
                                                            int iStockID)
        {
            return dal.GetDataSet("ICAdjustmentItems_SelectByProductSerialNoAndStockID",
                                                         strProductSerialNo,
                                                         iStockID);
        }

        public ICAdjustmentItemsInfo GetAdjustmentItemByProductSerialNoAndStockID(
                                                            String strProductSerialNo,
                                                            int iStockID)
        {
            return (ICAdjustmentItemsInfo)dal.GetDataObject("ICAdjustmentItems_SelectByProductSerialNoAndStockID",
                                                         strProductSerialNo,
                                                         iStockID);
        }

        public DataSet GetAdjustmentItemsByProductSerialNo(String strProductSerialNo)
        {
            return dal.GetDataSet("ICAdjustmentItems_SelectByProductSerialNo", strProductSerialNo);
        }

        public ICAdjustmentItemsInfo GetAdjustmentItemByProductSerialNo(String strProductSerialNo)
        {
            return (ICAdjustmentItemsInfo)dal.GetDataObject("ICAdjustmentItems_SelectByProductSerialNo", strProductSerialNo);
        }

        public DataSet GetAdjustmentItemsByProductID(int iProductID)
        {
            return dal.GetDataSet("ICAdjustmentItems_SelectByProductID", iProductID);
        }

        public ICAdjustmentItemsInfo GetAdjustmentItemByProductID(int iProductID)
        {
            return (ICAdjustmentItemsInfo)dal.GetDataObject("ICAdjustmentItems_SelectByProductID", iProductID);
        }

        public DataSet GetAdjustmentItemsByProductIDAndStockID(int iProductID, int iStockID)
        {
            DataSet ds = GetAdjustmentItemsByProductID(iProductID);
            if (ds.Tables.Count > 0)
            {
                for (int i = 0; i < ds.Tables[0].Rows.Count; i++)
                {
                    ICAdjustmentItemsInfo objAdjustmentItemsInfo = (ICAdjustmentItemsInfo)GetObjectFromDataRow(ds.Tables[0].Rows[i]);
                    if (objAdjustmentItemsInfo != null)
                        if (objAdjustmentItemsInfo.FK_ICStockID != iStockID)
                        {
                            ds.Tables[0].Rows.RemoveAt(i);
                            i--;
                        }
                }
            }
            return ds;
        }

        public ICAdjustmentItemsInfo GetAdjustmentItemByProductIDAndStockID(int iProductID, int iStockID)
        {
            DataSet ds = GetAdjustmentItemsByProductIDAndStockID(iProductID, iStockID);
            if (ds.Tables.Count > 0)
                return (ICAdjustmentItemsInfo)dal.GetSingleObject(ds.Tables[0]);
            return null;
        }
    }
