    public class ICAdjustmentsController : BaseBusinessController
    {
        public ICAdjustmentsController()
        {
            dal = new DALBaseProvider("ICAdjustments", typeof(ICAdjustmentsInfo));
        }

        public DataSet GetAdjustmentsByDateRange(DateTime from, DateTime to)
        {
            String strQuery = String.Format("SELECT * FROM [dbo].[ICAdjustments] WHERE CONVERT(VARCHAR(10),[ICAdjustmentDate],112)>= CONVERT(VARCHAR(10),'{0}',112) AND CONVERT(VARCHAR(10),[ICAdjustmentDate],112) <= CONVERT(VARCHAR(10),'{1}',112) AND [AAStatus]='{2}'", from.ToString("yyyyMMdd"), to.ToString("yyyyMMdd"), BusinessObject.DefaultAAStatus);
            return GetDataSet(strQuery);
        }
    }
    #endregion
}