        protected override DataSet GetSearchData(ref string searchQuey)
        {
            ICProductWorkGroupsController objProductWorkGroupsController = new ICProductWorkGroupsController();
            ICProductWorkGroupsInfo searchObject = (ICProductWorkGroupsInfo)CurrentModuleEntity.SearchObject;
            DataSet ds = new DataSet();
            ds = objProductWorkGroupsController.GetProductWorkGroupListBySomeCriteria(searchObject.ICProductWorkGroupNo,
